
#!/bin/bash # Replace this with your actual deployment URL 👇 BASE_URL="https://hashtag-rotator-service.vercel.app" echo "🔎 Testing /api/health..." curl -s "$BASE_URL/api/health" | jq . || echo "❌ Failed to get /api/health" echo "🔁 Testing /api/cron..." curl -s "$BASE_URL/api/cron" | jq . || echo "❌ Failed to get /api/cron" echo "🌍 Testing /api/trends?region=kenya..." curl -s "$BASE_URL/api/trends?region=kenya" | jq . || echo "❌ Failed to get /api/trends?region=kenya" echo "🎯 Testing geo-routed /kenya-trends..." curl -s "$BASE_URL/kenya-trends" | jq . || echo "❌ Failed to get /kenya-trends" echo "✅ All endpoints tested!" 
